import streamlit as st
import pandas as pd
import pickle

st.write("Obesity Database")
st.image("data:image/jpeg;base64,")

# User inputs
gender = st.selectbox('Gender', ['Female', 'Male'])
age = st.number_input('Age', min_value=0, max_value=120, value=25)
height = st.number_input('Height (m)', min_value=0.0, max_value=3.0, value=1.75)
weight = st.number_input('Weight (kg)', min_value=0.0, max_value=300.0, value=70.0)
family_history = st.selectbox('Family History with Overweight', ['yes', 'no'])
favc = st.selectbox('Frequent Consumption of High Calorie Food (FAVC)', ['yes', 'no'])
fcvc = st.number_input('Frequency of Consumption of Vegetables (FCVC)', min_value=0.0, max_value=5.0, value=2.0)
ncp = st.number_input('Number of Main Meals (NCP)', min_value=1.0, max_value=10.0, value=3.0)
caec = st.selectbox('Consumption of Food Between Meals (CAEC)', ['no', 'Sometimes', 'Frequently', 'Always'])
smoke = st.selectbox('Do you smoke?', ['yes', 'no'])
ch2o = st.number_input('Daily Water Intake (L)', min_value=0.0, max_value=10.0, value=2.0)
scc = st.selectbox('Calories Monitoring (SCC)', ['yes', 'no'])
faf = st.number_input('Physical Activity Frequency (FAF)', min_value=0.0, max_value=7.0, value=1.0)
tue = st.number_input('Time using Technology (TUE)', min_value=0.0, max_value=24.0, value=2.0)
calc = st.selectbox('Consumption of Alcohol (CALC)', ['no', 'Sometimes', 'Frequently', 'Always'])
mtrans = st.selectbox('Transportation (MTRANS)', ['Automobile', 'Motorbike', 'Bicycle', 'Walking', 'Public_Transportation'])

# Create the input DataFrame
input_df = pd.DataFrame({
    'Gender': [gender], 'Age': [age], 'Height': [height], 'Weight': [weight],
    'family_history_with_overweight': [family_history], 'FAVC': [favc],
    'FCVC': [fcvc], 'NCP': [ncp], 'CAEC': [caec], 'SMOKE': [smoke],
    'CH2O': [ch2o], 'SCC': [scc], 'FAF': [faf], 'TUE': [tue],
    'CALC': [calc], 'MTRANS': [mtrans]
})

# One-hot encode categorical columns
input_df_encoded = pd.get_dummies(input_df, drop_first=True)

# Load the trained model
with open('obesity_model.pkl', 'rb') as f:
    loaded_model = pickle.load(f)

# Expected columns from the training model (removed 'Unnamed: 0')
expected_columns = [
    'Gender_Male', 'Age', 'Height', 'Weight', 'FCVC', 'NCP', 'CH2O', 'FAF', 'TUE', 
    'family_history_with_overweight_yes', 'FAVC_yes', 
    'CAEC_Sometimes', 'CAEC_Frequently', 'CAEC_Always', 'SMOKE_yes', 
    'SCC_yes', 'CALC_Sometimes', 'CALC_Frequently', 'MTRANS_Motorbike', 
    'MTRANS_Walking', 'MTRANS_Public_Transportation', 'MTRANS_Automobile', 
    'CAEC_no', 'CALC_Always', 'CALC_no', 'FAVC_no', 'Gender_Female', 
    'MTRANS_Bike', 'SCC_no', 'SMOKE_no', 'family_history_with_overweight_no'
]

# Ensure input data columns match the expected columns
input_df_encoded = input_df_encoded.reindex(columns=expected_columns, fill_value=0)

# Make prediction
prediction = loaded_model.predict(input_df_encoded)

# Display result
st.write(f"The predicted obesity level is: **{prediction[0]}**")

